AND IS_DEBIT = 0
AND AC.COMPANY_ID = IN_COMPANY_ID
AND ACCT.RELATED_ACCOUNT_ID = IN_ACCOUNT_ID

AND GL.GL_DATE BETWEEN IN_FROM_GL_DATE and IN_TO_GL_DATE 
